import React from 'react';
//import { Outlet } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
//import Directory from '../../components/directory/directory.component';
//import Button from "../../components/button/button.component";
import "../../components/category-item/category-item.styles.scss";

const Home = () => {
  const navigate = useNavigate();

  const images = [
    {
      id: 1,
      label: 'hats',
      filter:"hats",
      imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
    },
    {
      id: 2,
      label: 'jackets',
      filter:"jackets",
      imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
    },
    {
      id: 3,
      label: 'sneakers',
      filter:"sneakers",
      imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
    },
    {
      id: 4,
      label: 'womens',
      filter:"womens",
      imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
    },
    {
      id: 5,
      label: 'mens',
      filter:"mens",
      imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
    },
  ];

  const handleNavigation = (filter) => {
    navigate(`/shop?filter=${filter}`);
  }


  return (
    <div >
      <div className="category-container">
        {images.map((image)=>(
          <div 
          key={image.id} 
          className="background-image"     
          style={{
            backgroundImage:`url(${image.imageUrl})`, 
            // backgroundSize:'cover',
            // backgroundPosition:'center',
            // height:'200px', 
            // margin:'10px',
            // width:'100%',
            display:'flex',
             //flexWrap:'wrap',
            // alignItems:'center',
            //justifyContent:'center',
            // cursor:'pointer'
          }}
          onClick={()=>handleNavigation(image.filter)}>
            <div className="category-body-container">{image.label}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
