import { useState } from "react";
import ProductCard from "../../components/product-card/product-card.component";
import PRODUCTS from "../../shop-data.json";
import "./shop.styles.scss";
import Button from "../../components/button/button.component";

const Shop = () => {
  const [filteredProducts, setFilteredProducts] = useState(PRODUCTS);
  //Track active button
  const [activeButton, setActiveButton] = useState("all");
  //Track search term
  const [searchTerm, setSearchTerm] = useState("");

  //Filter by Caps
  const filterCaps = () => {
    const caps = PRODUCTS.filter((item) => item.category === "Cap");
    setFilteredProducts(caps);
    setActiveButton("caps"); //set active button
    setSearchTerm("");
  };

  //Filter by Caps
  const filterJackets = () => {
    const jackets = PRODUCTS.filter((item) => item.category === "Jacket");
    setFilteredProducts(jackets);
    setActiveButton("jackets"); //set active button
    setSearchTerm("");
  };

  //Filter by Sneakers
  const filterSneakers = () => {
    const sneakers = PRODUCTS.filter((item) => item.category === "Sneaker");
    setFilteredProducts(sneakers);
    setActiveButton("sneakers"); //set active button
    setSearchTerm("");
  };

  //Filter by Men
  const filterMen = () => {
    const men = PRODUCTS.filter((item) => item.gender === "men");
    setFilteredProducts(men);
    setActiveButton("men"); //set active button
    setSearchTerm("");
  };

  //Filter by Women
  const filterWomen = () => {
    const women = PRODUCTS.filter((item) => item.gender === "women");
    setFilteredProducts(women);
    setActiveButton("women"); //set active button
    setSearchTerm("");
  };

  //Show All Items
  const showAll = () => {
    setFilteredProducts(PRODUCTS);
    setActiveButton("all"); //set active button
    setSearchTerm("");
  };

  //handle search filtering
  const handleSearch = (e) => {
    const term = e.target.value.toLowerCase();
    setSearchTerm(term);

    //dynamically filter based on search term & active button
    const filtered = PRODUCTS.filter(
      (product) =>
        product.name.toLowerCase().includes(term) ||
        product.category.toLowerCase().includes(term) ||
        product.gender.toLowerCase().includes(term)
    );

    setFilteredProducts(filtered);
    setActiveButton(""); //clear active button when searching
  };

  return (
    <div style={{ flex: 1 }}>
      {/* Search Bar with Button*/}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "10px",
          marginBottom: "20px",
        }}
      >
        <input
          type="text"
          placeholder="Search for products..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{
            padding: "10px",
            width: "300px",
            border: "1px solid #ddd",
            fontSize: "16px",
          }}
        />
        <Button onClick={handleSearch} buttonType="inverted">
          Search
        </Button>
        <Button onClick={showAll} buttonType="inverted">
          Reset
        </Button>
      </div>
      {/* Buttons */}
      {/* <div style={{ display: "flex", gap: "5px", justifyContent: "center" }}>
        <Button
          onClick={showAll}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "all" ? "" : "inverted"}
        >
          ALL
        </Button>
        <Button
          onClick={filterCaps}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "caps" ? "" : "inverted"}
        >
          {" "}
          CAPS{" "}
        </Button>
        <Button
          onClick={filterJackets}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "jackets" ? "" : "inverted"}
        >
          {" "}
          JACKETS{" "}
        </Button>
        <Button
          onClick={filterSneakers}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "sneakers" ? "" : "inverted"}
        >
          {" "}
          SNEAKERS{" "}
        </Button>
        <Button
          onClick={filterMen}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "mens" ? "" : "inverted"}
        >
          {" "}
          MENS{" "}
        </Button>
        <Button
          onClick={filterWomen}
          style={{ fontFamily: "Open Sans Condensed" }}
          buttonType={activeButton === "womens" ? "" : "inverted"}
        >
          {" "}
          WOMENS{" "}
        </Button>
      </div> */}
      &nbsp;
      {/* Product List */}
      {/* <div className="products-container">
        {filteredProducts.map((product) => (
          <ProductCard key={product.id} product={product} />
        ))}
      </div> */}
      <div className="products-container">
        {filteredProducts.length > 0 ? (
          filteredProducts.map((product) => (
            <ProductCard key={product.id} product={product} />
          ))
        ) : (
          <div
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              alignContent: "center",
              height: "50vh",
              width: "195vh",
              textAlign: "center",
              //backgroundColor: "lime",
              //alignSelf: "center",
              //marginLeft: "40vh",
            }}
            //className="products-container"
          >
            <h3 style={{ margin: "10px 0" }}>No products found..!!</h3>
            <p style={{ margin: "0" }}>
              Try adjusting your search or filter criteria.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Shop;
