import React from 'react';
//import { Outlet } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
//import Directory from '../../components/directory/directory.component';
//import Button from "../../components/button/button.component";
import '../../components/category-item/category-item.styles.scss';

const Home = () => {
  const navigate = useNavigate();

  // const goToShop = () => {
  //   navigate("/shop");
  // };

  const images = [
    {
      id: 1,
      label: 'hats',
      filter:"hats",
      imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
    },
    {
      id: 2,
      label: 'jackets',
      filter:"jackets",
      imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
    },
    {
      id: 3,
      label: 'sneakers',
      filter:"sneakers",
      imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
    },
    {
      id: 4,
      label: 'womens',
      filter:"womens",
      imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
    },
    {
      id: 5,
      label: 'mens',
      filter:"mens",
      imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
    },
  ];

  const handleNavigation = (filter) => {
    navigate(`/shop?filter=${filter}`);
  }


  return (
    <div >
      <div 
      //className="category-container"
      style={{
        minWidth:'30%',
        height:'240px',
        flex:'1 1 auto',
        display:'flex',
        alignItems:'center',
        justifyContent:'center',
        border: '1px solid black',
        margin:'0 7.5px 15px',
        overflow:'hidden',
        cursor:'pointer',
      }}>
        {images.map((image)=>(
          <div 
          //className="background-image"
          key={image.id}
          style={{
            backgroundImage:`url(${image.imageUrl})`, 
            backgroundSize:'cover',
            width:'100%',
            height:'100%',
            backgroundPosition:'center',
          }}
          onClick={()=>handleNavigation(image.filter)}>
            <div 
            //className="category-body-container"
            style={{
              color:'#fff',
              backgroundColor:"rgba(0,0,0,0.5)",
              padding:'5px',
              height:'90px',
              padding:'0 25px',
              display:'flex',
              flexDirection:'column',
              alignItems:'center',
              justifyContent:'center',
              border:'1px solid black',
              backgroundColor:'white',
              opacity:'0.7',
              position:'absolute'
              }}
              >
               <h2 style={{fontWeight:'bold',margin:'0 6px 0',fontSize:'22px',color:'#4a4a4a'}}>
                {image.label}
                </h2> 
                </div>
          </div>
        ))}
      </div>
      {/* <div style={{display:'flex',justifyContent:'center',backgroundColor:'transparent',}}>
          <Button onClick={goToShop} >STILL THINKING?? GO AND EXPLORE ðŸ‘‰ðŸ‘‰ HERE ðŸ‘ˆðŸ‘ˆ</Button>
      </div> */}
    </div>
  );
};

export default Home;
