import {  useState } from 'react';
import ProductCard from '../../components/product-card/product-card.component';
//import { ProductsContext } from '../../contexts/products.context';
import PRODUCTS from '../../shop-data.json';
import './shop.styles.scss';

const Shop = () => {
  //const { products } = useContext(ProductsContext);

  const [filteredProducts, setFilteredProducts] = useState(PRODUCTS);
  //Track active button
  const [activeButton,setActiveButton]= useState("all");

  //Filter by Caps
  const filterCaps = () => {
    const caps = PRODUCTS.filter((item)=>item.category === "Cap");
    setFilteredProducts(caps);
    setActiveButton("caps");  //set active button
  }

  //Filter by Caps
  const filterJackets = () => {
    const jackets = PRODUCTS.filter((item)=>item.category === "Jacket");
    setFilteredProducts(jackets);
    setActiveButton("jackets");  //set active button
  }

  //Filter by Sneakers
  const filterSneakers = () => {
    const sneakers = PRODUCTS.filter((item)=>item.category === "Sneaker");
    setFilteredProducts(sneakers);
    setActiveButton("sneakers");  //set active button
  }

  //Filter by Men
  const filterMen = () => {
    const men = PRODUCTS.filter((item)=>item.gender === "men");
    setFilteredProducts(men);
    setActiveButton("men");  //set active button
  }

  //Filter by Women
  const filterWomen = () => {
    const women = PRODUCTS.filter((item)=>item.gender === "women");
    setFilteredProducts(women);
    setActiveButton("women");  //set active button
  }

  //Show All Items
  const showAll = () => {
    setFilteredProducts(PRODUCTS);
    setActiveButton("all");  //set active button
  }

  return (
  <div style={{flex:1}}>
    {/* Buttons */}
   <div style={{display:'flex',gap:'5px',justifyContent:'center'}} >
    <button onClick={showAll} style={{fontFamily:'Open Sans Condensed',fontSize:'15px',opacity:activeButton==="all"?1:0.5}}> ALL </button>
    <button onClick={filterCaps} style={{fontSize:'15px',opacity:activeButton==="caps"?1:0.5}}> ðŸ§¢ </button>
    <button onClick={filterJackets} style={{fontSize:'15px',opacity:activeButton==="jackets"?1:0.5}}> ðŸ§¥ </button>
    <button onClick={filterSneakers} style={{fontSize:'15px',opacity:activeButton==="sneakers"?1:0.5}}> ðŸ‘Ÿ </button>
    <button onClick={filterMen} style={{fontSize:'15px',opacity:activeButton==="men"?1:0.5}}> ðŸš¹ </button>
    <button onClick={filterWomen} style={{fontSize:'15px',opacity:activeButton==="women"?1:0.5}}> ðŸšº </button>
    </div>

    &nbsp;
    {/* Product List */}
    <div className='products-container'>
      {filteredProducts.map((product) => (
        <ProductCard key={product.id} product={product} />
      ))}
    </div>
   </div>

  );
};

export default Shop;














