import React from 'react';
//import { Outlet } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
//import Directory from '../../components/directory/directory.component';
//import Button from "../../components/button/button.component";

const Home = () => {
  const navigate = useNavigate();

  // const goToShop = () => {
  //   navigate("/shop");
  // };

  const images = [
    {
      id: 1,
      label: 'hats',
      filter:"hats",
      imageUrl: 'https://i.ibb.co/cvpntL1/hats.png',
    },
    {
      id: 2,
      label: 'jackets',
      filter:"jackets",
      imageUrl: 'https://i.ibb.co/px2tCc3/jackets.png',
    },
    {
      id: 3,
      label: 'sneakers',
      filter:"sneakers",
      imageUrl: 'https://i.ibb.co/0jqHpnp/sneakers.png',
    },
    {
      id: 4,
      label: 'womens',
      filter:"womens",
      imageUrl: 'https://i.ibb.co/GCCdy8t/womens.png',
    },
    {
      id: 5,
      label: 'mens',
      filter:"mens",
      imageUrl: 'https://i.ibb.co/R70vBrQ/men.png',
    },
  ];

  const handleNavigation = (filter) => {
    navigate(`/shop?filter=${filter}`);
  }


  return (
    <div >
      <div >
        {images.map((image)=>(
          <div key={image.id}
          className='image-box'
          style={{
            backgroundImage:`url(${image.imageUrl})`, 
            backgroundSize:'cover',
            backgroundPosition:'center',
            height:'200px', 
            margin:'10px',
            width:'100%',
            display:'flex',
            flexWrap:'wrap',
            flexDirection:'row',
            alignItems:'center',
            justifyContent:'center',
            cursor:'pointer'
          }}
          onClick={()=>handleNavigation(image.filter)}>
            <span style={{color:'#fff',backgroundColor:"rgba(0,0,0,0.5)",padding:'5px'}}>{image.label}</span>
          </div>
        ))}
      </div>
      {/* <div style={{display:'flex',justifyContent:'center',backgroundColor:'transparent',}}>
          <Button onClick={goToShop} >STILL THINKING?? GO AND EXPLORE ðŸ‘‰ðŸ‘‰ HERE ðŸ‘ˆðŸ‘ˆ</Button>
      </div> */}
    </div>
  );
};

export default Home;
